var EventEmitter = require('eventemitter2').EventEmitter2,
    util =require('util'),
    _ = require('underscore'),
    Card = require('logic/Card/Card');

var Player = function(game) {
    this.cards = Card.createCardsForPlayer(this, game);
    this.money = 0;
    this.resources = {
        sand: 0,
        wood: 0,
        bricks: 0,
        stones: 0,
        silver: 0
    };
};

util.inherits(Player, EventEmitter);

_(Player.prototype).extend({
    receiveMoney: function(amount) {
        this.money += amount;
        this.emit('money.received', {
            amount: amount,
            total: this.money
        });
    }
});

module.exports = Player;
