var _ = require('underscore');

var Treasury = function() {
    this.resources = {
        sand: 10,
        wood: 10,
        bricks: 10,
        stones: 10,
        silver: 10
    };
    this.money = 100;
};

_(Treasury.prototype).extend({
    getMoney: function(amount) {
        var result = Math.min(this.money, amount);

        this.money -= result;

        return result;
    },

    putMoney: function(amount) {
        this.money += amount;
    }
});

module.exports = Treasury;
